bool isPalindrome(std::string s) {
    std::string cleanStr = "";
    // Convert to lowercase and remove non-alphanumeric characters
    for (char c : s) {
        if (std::isalnum(c)) {
            cleanStr += std::tolower(c);
        }
    }
    // Check if palindrome
    int left = 0, right = cleanStr.size() - 1;
    while (left <= right) {
        if (cleanStr[left++] != cleanStr[right--])
            return false;
    }
    return true;
